<template>
  <vp-titre titre="Catalogue"/>
  <div>
    <div class="m-2 flex" v-for="pc in catalogue" :key="pc.produit.id">
      <vp-produit class="flex-grow" :produit="pc.produit"></vp-produit>
      <span>{{ pc.stock }}</span> 
      <button :disabled="pc.stock==0" class="border-2 p-2 rounded hover:bg-gray-100" @click="ajouterAuPanier(pc.produit.id)">Ajouter au panier</button>
    </div>
  </div>
</template>

<script lang="ts">
  import { defineComponent } from "vue";
  import VPProduit from "@/components/common/Produit.vue"
  import VPTitre from "@/components/common/Titre.vue"

  export default defineComponent({
    props: ['catalogue'],
    methods: {
      ajouterAuPanier(id: number) {
        this.$emit('ajouter-au-panier', id)
      }
    },
    components: {
      "vp-produit": VPProduit,
      "vp-titre": VPTitre
    }  
  })                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
